import React, { useState } from 'react'
import { db, auth } from '../firebase'
import firebase from 'firebase'
import { Input, Button } from '@material-ui/core'

function SendMessage({scroll}) {
  const [msg, setMsg] = useState('')

  async function sendMessage(e) {
    e.preventDefault()
    const { uid, photoURL } = auth.currentUser

    await db.collection('messages').add({
      text: msg,
      photoURL,
      uid,
      createdAt: firebase.firestore.FieldValue.serverTimestamp()
    })
    setMsg('')
    scroll.current.scrollIntoView({behavior: 'smooth'})
  }

  return (
    <div>
      <form onSubmit={sendMessage}>
        <Input style={{ width: '78%', fontSize: '15px', fontWeight: '550', marginLeft: '5px', marginBottom: '-3px' }} value={msg} onChange={(e) => setMsg(e.target.value)} placeholder="Send a message" />
        <Button style={{ width: '20%', fontSize: '15px', fontWeight: '550'}} type="submit">Chat</Button>
      </form>
    </div>
  )
}

export default SendMessage
