import React from 'react'
import firebase from 'firebase'
import { auth } from '../firebase.js'
import { Button, colors } from '@material-ui/core'

function SignIn() {
  function signInWithGoogle() {
    const provider = new firebase.auth.GoogleAuthProvider()
    auth.signInWithPopup(provider)
  }
  return (
    <div style={{
      display: 'flex', justifyContent: 'center', position: 'fixed', width: '100%', backgroundColor: 'white', top: 0, borderBottom: 'solid 1px lightgray', zIndex: '10'
    }}>
      <Button style={{ padding: '20px', fontSize: '15px', borderRadius: '0', fontWeight: '600' }} onClick={signInWithGoogle} >Sign In With Google</Button>
    </div>
  )
}

export default SignIn
