import React from 'react'
import firebase from 'firebase'
import { auth } from '../firebase.js'
import { Button } from '@material-ui/core'

function SignIn() {
  function signInWithGoogle() {
    const provider = new firebase.auth.GoogleAuthProvider()
    auth.signInWithPopup(provider)
  }
  return (
    <div style={{
      justifyContent: 'center', position: 'fixed', width: '100%', height: '100%', backgroundColor: '#18181B'}}>
      <div class="bar-container">
        <p class="title">Chat Room App</p>
      </div>
      <div>
        <div class="container">
          <Button style={{ padding: '20px', fontSize: '15px', borderRadius: '0', color: 'whitesmoke', fontWeight: '400' }} onClick={signInWithGoogle} >Sign In With Google</Button>
        </div>
      </div>
    </div>
  )
}

export default SignIn
